import { useRouter } from 'next/router'
import { useEffect, useState } from 'react'

export default function Login({ theme, setTheme }) {
  const router = useRouter()
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  useEffect(() => {
    // if already logged in, redirect to dashboard
    if (typeof window !== 'undefined' && window.localStorage.getItem('user')) {
      router.push('/dashboard')
    }
  }, [router])

  const onSubmit = (e) => {
    e.preventDefault()
    // Mock auth: accept any non-empty username/password
    if (!username || !password) {
      alert('Enter username and password (mock).')
      return
    }
    localStorage.setItem('user', JSON.stringify({ username }))
    router.push('/dashboard')
  }

  return (
    <div className="container" style={{maxWidth:420}}>
      <div className="card" style={{marginTop:40}}>
        <h2>Sign in (mock)</h2>
        <form onSubmit={onSubmit} style={{display:'grid',gap:10}}>
          <input className="input" placeholder="Username" value={username} onChange={(e)=>setUsername(e.target.value)} />
          <input className="input" placeholder="Password" type="password" value={password} onChange={(e)=>setPassword(e.target.value)} />
          <button className="btn" type="submit">Sign in</button>
        </form>
        <p className="small" style={{marginTop:12}}>This is mock authentication for the assignment. Any non-empty credentials will work.</p>
      </div>
    </div>
  )
}
